package permutation_in_string;

public class Solution {
    public static void main(String[] args) {
        boolean include = checkInclusion(
                "",
                ""
        );
        System.out.println(include);
    }

    /**
     * the main idea is that permutation has same characters, so we just need a array to store the character count
     * @param s1
     * @param s2
     * @return
     */
    public static boolean checkInclusion(String s1, String s2) {
        if (s1.length() > s2.length()) {
            return false;
        }
        int[] alphabet = new int[26];
        //first we count all characters in s1
        for (char c : s1.toCharArray()) {
            alphabet[c - 'a']++;
        }
        //use count the represent the left character to be matched ,so we init it with s1.length
        int count = s1.length();
        char[] chs = s2.toCharArray();
        for (int i = 0; i < chs.length; i++) {
            char c = chs[i];
            //since the window size has reached s1.length, so we should kick out
            //the left most character as the pointer move right.if the count is less
            //than 0, it means the left most character is enough in substring, so
            //there is no need to increase the left character to be matched.
            if (i >= s1.length() && alphabet[chs[i - s1.length()] - 'a']++ >= 0) {
                count++;
            }
            if (alphabet[c - 'a']-- > 0) {
                count--;
            }
            if (count == 0) {
                return true;
            }
        }
        return count == 0;
    }
}
